import React, { useState, useEffect } from "react";
import "./App.css";
import api from "./api";

const App = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    // get query
    api.get(`/todo`).then(res => setData(res.data));
  }, [data]);

  // post query
  const _onSubmit = e => {
    e.preventDefault();
    const data = {
      name: e.target.elements.name.value,
      age: Number(e.target.elements.age.value)
    };
    // console.log(this.state.val);
    // api.post("/todo", data).then(res => {
    //   const newArr = [...arr, res.data];
    //   this.setState({ val: newArr });
    // });
  };

  // delete query
  const _onDelete = id => {
    api.delete(`/todo/${id}`).then(() => {
      api.get("/todo").then(res => {
        setData([], () => setData(res.data));
        // this.setState({ val: [] }, () => this.setState({ val: res.data }));
      });
    });
  };

  // edit/put query
  const _onEdit = (key, id) => {
    // const arr = this.state.val;
    const data = {
      // name: this.state.val
    };
    api.put(`/todo/${id}`).then(res => console.log("edit item", res.data));
  };
  return (
    <div className="App">
      <form onSubmit={_onSubmit}>
        <input
          type="text"
          placeholder="Name"
          name="name"
          style={{ marginTop: "2em" }}
        />
        <br />
        <input
          type="text"
          placeholder="Age"
          name="age"
          style={{ marginTop: "2em" }}
          width="105px"
        />
        <button type="submit">Add Todo</button>
        <table
          style={{
            display: "flex",
            alignItems: "center",
            flexDirection: "column",
            marginTop: "1em"
          }}
        >
          {!!data.length &&
            data.map((i, key) => (
              <tr key={key}>
                <td contentEditable={false}>Name: {i.name} == </td>
                <td contentEditable={false}>
                  Age: {i.age}
                  <button onClick={() => _onDelete(i._id)}>delete</button>
                  <button onClick={() => _onEdit(key, i._id)}>edit</button>
                </td>
              </tr>
            ))}
        </table>
      </form>
    </div>
  );
};

export default App;
