import api from "./Api";

//////////////// get query ////////////////
const getData = () => api.get(`/todo`).then(res => res.data);

//////////////// post query ////////////////
const postData = e => {
  e.preventDefault();
  const todo = {
    name: e.target.elements.name.value,
    age: Number(e.target.elements.age.value)
  };
  api.post("/todo", todo).then(res => res.data);
};

