import React from "react";
import "./App.css";
import api from "./api";
// import axios from "axios";

export default class App extends React.Component {
  state = {
    val: []
  };

  // get query
  componentDidMount() {
    api.get(`/todo`).then(res => {
      this.setState({ val: res.data });
      // console.log(res.data);
    });
  }

  // post query
  _onSubmit(e) {
    e.preventDefault();
    const data = {
      name: e.target.elements.name.value,
      age: Number(e.target.elements.age.value)
    };
    console.log(data);
    api.post("/todo", data).then(res => {
      const arr = [...this.state.val, res.data];
      this.setState({ val: arr });
    });
  }

  // delete query
  _onDelete(id) {
    api.delete(`/todo/${id}`).then(() => {
      api.get("/todo").then(res => {
        this.setState({ val: [] }, () => this.setState({ val: res.data }));
      });
    });
  }

  // edit/put query
  _onEdit(key, id) {
    const arr = this.state.val;

    const data = {
      name: this.state.val
    };
    api.put(`/todo/${id}`).then(res => console.log("edit item", res.data));
  }
  render() {
    return (
      <div className="App">
        <form onSubmit={this._onSubmit}>
          <input
            type="text"
            placeholder="Name"
            name="name"
            style={{ marginTop: "2em" }}
          />
          <br />
          <input
            type="text"
            placeholder="Age"
            name="age"
            style={{ marginTop: "2em" }}
          />
          <button type="submit">Add Todo</button>
          <table
            style={{
              display: "flex",
              alignItems: "center",
              flexDirection: "column",
              marginTop: "1em"
            }}
          >
            {!!this.state.val.length &&
              this.state.val.map((i, key) => (
                <tr key={key}>
                  <td contentEditable={false}>Name: {i.name} == </td>
                  <td contentEditable={false}>
                    Age: {i.age}
                    <button onClick={() => this._onDelete(i._id)}>
                      delete
                    </button>
                    <button onClick={() => this._onEdit(key, i._id)}>
                      edit
                    </button>
                  </td>
                </tr>
              ))}
          </table>
        </form>
      </div>
    );
  }
}
