import api from "./Api";

//////////////// get query ////////////////
const get = () => api.get(`/todo`).then(res => res.data);

//////////////// post query ////////////////
const post = e => {
  e.preventDefault();
  const todo = {
    name: e.target.elements.name.value,
    age: Number(e.target.elements.age.value)
  };
  api.post("/todo", todo).then(res => {
    res.data;
    e.target.elements.name.value = "";
    e.target.elements.age.value = "";
  });
};

// delete query
const _onDelete = id => {
  api.delete(`/todo/${id}`).then(() => {
    api.get("/todo").then(res => {
      setData([], () => setData(res.data));
    });
  });
};

export { get, post };
