import React, { useState, useEffect } from "react";
import "./App.css";
import api from "./Api";
import { get } from "./Methods";

const App = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    // // get query
    get().then(a => setData(a));
    console.log(data);
    // api.get(`/todo`).then(res => setData(res.data));
  },[]);

  // post query
  const _onSubmit = e => {
    e.preventDefault();
    const todo = {
      name: e.target.elements.name.value,
      age: Number(e.target.elements.age.value)
    };
    api.post("/todo", todo).then(res => setData(res.data));
  };

  // delete query
  const _onDelete = id => {
    api.delete(`/todo/${id}`).then(() => {
      api.get("/todo").then(res => {
        setData([], () => setData(res.data));
      });
    });
  };

  // edit/put query
  const _onEdit = (key, id) => {
    // const arr = this.state.val;
    const data = {
      // name: this.state.val
    };
    api.put(`/todo/${id}`).then(res => console.log("edit item", res.data));
  };

  const useData = () => {
    return data.length ? (
      data.map((i, key) => (
        <tr key={key}>
          <td contentEditable={false}>Name: {i.name} == </td>
          <td contentEditable={false}>
            Age: {i.age}
            <button onClick={() => _onDelete(i._id)}>delete</button>
            <button onClick={() => _onEdit(key, i._id)}>edit</button>
          </td>
        </tr>
      ))
    ) : (
      <p>Loading...</p>
    );
  };
  return (
    <div className="App">
      <form onSubmit={_onSubmit}>
        <input
          type="text"
          placeholder="Name"
          name="name"
          style={{ marginTop: "2em" }}
        />
        <br />
        <input
          type="text"
          placeholder="Age"
          name="age"
          style={{ width: 105, marginTop: "1em" }}
        />
        <button type="submit">Add Todo</button>
        <table
          style={{
            display: "flex",
            alignItems: "center",
            flexDirection: "column",
            marginTop: "1em"
          }}
        >
          {useData()}
        </table>
      </form>
    </div>
  );
};

export default App;
