import api from "./Api";

//////////////// get query ////////////////
const get = () => api.get(`/todo`).then(res => res.data);

//////////////// post query ////////////////
const post = e => {
  e.preventDefault();
  const todo = {
    name: e.target.elements.name.value,
    age: Number(e.target.elements.age.value)
  };
  api.post("/todo", todo).then(res => res.data);
};

export { get, post };
