import React from "react";
import "./App.css";
import api from "./api";
// import axios from "axios";

export default class App extends React.Component {
  state = {
    val: [],
    name: "",
    age: 0
  };
  componentDidMount() {
    api.get(`/todo`).then(res => {
      this.setState({ val: res.data });
      // console.log(res);
    });
  }
  _onSubmit() {
    const data = {
      name: this.state.name,
      age: this.state.age
    };
    // post query
    api.post("/todo", data).then(res => {
      const arr = [...this.state.val, res.data];
      this.setState({ val: arr });
    });
  }
  // delete query
  _onDelete(id) {
    api.delete(`/todo/${id}`).then(() => {
      api
        .get("/todo")
        .then(res => this.setState({ val: [...this.state.val, res.data] }));
    });
  }
  render() {
    return (
      <div className="App">
        <input
          type="text"
          placeholder="Name"
          onChange={e => this.setState({ name: e.target.value })}
          style={{ marginTop: "2em" }}
        />
        <br />
        <input
          type="text"
          placeholder="Age"
          onChange={e => this.setState({ age: Number(e.target.value) })}
          style={{ marginTop: "2em" }}
        />
        <button type="submit" onClick={() => this._onSubmit()}>
          Add Todo
        </button>
        <table
          style={{
            display: "flex",
            alignItems: "center",
            flexDirection: "column",
            marginTop: "1em"
          }}
        >
          {!!this.state.val.length &&
            this.state.val.map((i,key) => (
              <tr key={key}>
                <td>Name: {i.name} == </td>
                <td>
                  Age: {i.age}
                  <button onClick={() => this._onDelete(i._id)}>delete</button>
                </td>
              </tr>
            ))}
        </table>
      </div>
    );
  }
}
