import React, {useEffect, useState} from "react";
import "./App.css";
import api from "./api";

export const App = () => {
  const [data, setData] = useState([]);
    // state = {
    //   val: []
    // };
   useEffect =()=>{
     api.get(`/todo`).then(res => {
       setData(res.data)
      //  this.setState({ val: res.data });
     });

   }
  // get query
  // componentDidMount() {
  // }

  // post query
  _onSubmit = e => {
    e.preventDefault();
    const data = {
      name: e.target.elements.name.value,
      age: Number(e.target.elements.age.value)
    };
    console.log(this.state.val);
    // api.post("/todo", data).then(res => {
    //   const newArr = [...arr, res.data];
    //   this.setState({ val: newArr });
    // });
  }

  // delete query
  _onDelete = id => {
    api.delete(`/todo/${id}`).then(() => {
      api.get("/todo").then(res => {
        this.setState({ val: [] }, () => this.setState({ val: res.data }));
      });
    });
  }

  // edit/put query
  _onEdit =(key, id)=> {
    const arr = this.state.val;

    const data = {
      name: this.state.val
    };
    api.put(`/todo/${id}`).then(res => console.log("edit item", res.data));
  }
    return (
      <div className="App">
        <form onSubmit={this._onSubmit}>
          <input
            type="text"
            placeholder="Name"
            name="name"
            style={{ marginTop: "2em" }}
          />
          <br />
          <input
            type="text"
            placeholder="Age"
            name="age"
            style={{ marginTop: "2em" }}
            width="105px"
          />
          <button type="submit">Add Todo</button>
          <table
            style={{
              display: "flex",
              alignItems: "center",
              flexDirection: "column",
              marginTop: "1em"
            }}
          >
            {!!this.state.val.length &&
              this.state.val.map((i, key) => (
                <tr key={key}>
                  <td contentEditable={false}>Name: {i.name} == </td>
                  <td contentEditable={false}>
                    Age: {i.age}
                    <button onClick={() => this._onDelete(i._id)}>
                      delete
                    </button>
                    <button onClick={() => this._onEdit(key, i._id)}>
                      edit
                    </button>
                  </td>
                </tr>
              ))}
          </table>
        </form>
      </div>
    );
}
