import React, { useState, useEffect } from "react";
import "./App.css";
import api from "./Api";
import { _onGet, _onPost, _onDelete } from "./Methods";

const App = () => {
  const [data, setData] = useState([]);
  useEffect(() => {
    _onGet().then(res => setData(res));
  }, [data]);

  const useData = () => {
    return data.length ? (
      data.map((i, key) => (
        <tr key={key}>
          <td contentEditable={false}>Name: {i.name} == </td>
          <td contentEditable={false}>
            Age: {i.age}
            <button onClick={() => _delete(i._id)}>delete</button>
            <button onClick={() => _onEdit(key, i._id)}>edit</button>
          </td>
        </tr>
      ))
    ) : (
      <p>Loading...</p>
    );
  };
  return (
    <div className="App">
      <form onSubmit={_onPost}>
        <input
          type="text"
          placeholder="Name"
          name="name"
          style={{ marginTop: "2em" }}
        />
        <br />
        <input
          type="text"
          placeholder="Age"
          name="age"
          style={{ width: 105, marginTop: "1em" }}
        />
        <button type="submit">Add Todo</button>
        <table
          style={{
            display: "flex",
            alignItems: "center",
            flexDirection: "column",
            marginTop: "1em"
          }}
        >
          {useData()}
        </table>
      </form>
    </div>
  );
};

export default App;
