import React from "react";
import "./App.css";
import api from "./api";
// import axios from "axios";

export default class App extends React.Component {
  state = {
    val: [],
    name: "",
    age: 0
  };
  componentDidMount() {
    api.get(`/todo`).then(res => {
      this.setState({ val: res.data });
      // console.log(res);
    });
  }
  _onSubmit() {
    const data = {
      name: this.state.name,
      age: this.state.age
    };
    api
      .post("/todo", data)
      .then(res => {
        const arr = [...this.state.val, res.data];
        this.setState({ val: arr });
        console.log(res.data);
      })
      .catch(err => console.log(err));
  }
  render() {
    return (
      <div className="App">
        <input
          type="text"
          placeholder="Name"
          onChange={e => this.setState({ name: e.target.value })}
          style={{ marginTop: "2em" }}
        />
        <br />
        <input
          type="text"
          placeholder="Age"
          onChange={e => this.setState({ age: Number(e.target.value) })}
          style={{ marginTop: "2em" }}
        />
        <button type="submit" onClick={() => this._onSubmit()}>
          Add Todo
        </button>
        <table
          style={{
            display: "flex",
            alignItems: "center",
            flexDirection: "column",
            marginTop: "1em"
          }}
        >
          {!!this.state.val.length &&
            this.state.val.map(i => (
              <tr key={i._id}>
                <td>{i.name}</td>
                <td>{i.age}</td>
              </tr>
            ))}
        </table>
      </div>
    );
  }
}
