import api from "./Api";

//////////////// get query ////////////////
const useDataGet = () => api.get(`/todo`).then(res => res.data);

//////////////// post query ////////////////
const useDataPost = e => {
  e.preventDefault();
  const todo = {
    name: e.target.elements.name.value,
    age: Number(e.target.elements.age.value)
  };
  api.post("/todo", todo).then(res => res.data);
};

// delete query
const useDataDelete = id => {
  api.delete(`/todo/${id}`).then(() => {
    api.get("/todo").then(res => {
      setData([], () => setData(res.data));
    });
  });
};

export { useDataGet, useDataPost };
