import React from "react";
import "./App.css";
import api from "./api";
// import axios from "axios";

export default class App extends React.Component {
  state = {
    val: []
  };
  componentDidMount() {
    api.get(`/todo`).then(res => {
      this.setState({ val: res.data });
      console.log(res);
    });
  }
  render() {
    return (
      <div className="App">
        <input
          type="text"
          // onChange={e => setVal(e.target.value)}
          style={{ marginTop: "2em" }}
        />
        <button type="submit">Add Todo</button>
        <table
          style={{
            display: "flex",
            alignItems: "center",
            flexDirection: "column",
            marginTop: "1em"
          }}
        >
          {!!this.state.val.length &&
            this.state.val.map(i => (
              <tr key={i._id}>
                <td>{i.name}</td>
              </tr>
            ))}
        </table>
      </div>
    );
  }
}
